INSERT INTO Recipes (name, short_desc , recipe_type, difficulty_level, TIP1 ,TIP2 ,TIP3, prep_time, cook_time, cuisine_id) VALUES ('Albanian Fërgesë', 'Traditional Albanian dish featuring baked peppers, tomatoes, onions, and feta cheese, seasoned with herbs and served hot with crusty bread.', 'gastronomy', '2', 'Roast peppers until charred, then peel off the skin for a smoky flavor and tender texture.', 'Sauté onions and tomatoes until softened before combining with roasted peppers and feta cheese for a rich and savory filling.', 'Bake Fërgesë until bubbly and golden brown on top, then garnish with chopped parsley for freshness before serving.', '00:30:00', '00:45:00', 27);
